        "\tnuc.coupon_status couponStatus,\n" +
        "\tnuc.coupon_number couponNumber,\n" +
        "\tnu.username userName\n" +
        "FROM\n" +
        "\tnideshop_user_coupon nuc\n" +
        "LEFT JOIN nideshop_user nu ON nuc.user_id = nu.id where 1=1 $start$";
//			优惠券编号
if(StringUtil.isNotEmpty(conditionMap.get("couponNumber"))){
    sql=sql+"and nuc.coupon_number="+"'"+conditionMap.get("couponNumber")+"'";
}
//			用户名
if(StringUtil.isNotEmpty(conditionMap.get("userName"))){
    sql=sql+"and nu.username="+"'"+conditionMap.get("userName")+"'";
}
//			优惠券状态
if(StringUtil.isNotEmpty(conditionMap.get("couponStatus"))){
    sql=sql+"and nuc.coupon_status="+"'"+conditionMap.get("couponStatus")+"'";
}
//			添加时间  begin
if(StringUtil.isNotEmpty(conditionMap.get("addTimeBegin"))){
    sql=sql+"and nuc.add_time>="+"'"+conditionMap.get("addTimeBegin")+"'";
}
//			添加时间 end
if(StringUtil.isNotEmpty(conditionMap.get("addTimeEnd"))){
    sql=sql+"and nuc.add_time<="+"'"+conditionMap.get("addTimeEnd")+"'";
}
//			使用时间  begin
if(StringUtil.isNotEmpty(conditionMap.get("usedTimeBegin"))){
    sql=sql+"and nuc.used_time>="+"'"+conditionMap.get("usedTimeBegin")+"'";
}
//			使用时间  end
if(StringUtil.isNotEmpty(conditionMap.get("usedTimeEnd"))){
    sql=sql+"and nuc.used_time<="+"'"+conditionMap.get("usedTimeEnd")+"'";
}
int totalPage=1;
log.error(sql);
if(dataGrid!=null){
    List<Map<String, Object>> totalList = super.findForJdbc("select count(0) total from (" + sql + ")t");
    Integer total = Integer.valueOf(totalList.get(0).get("total") + "");
    dataGrid.setTotal(total);
    if(total>0){
        resultMapList= super.findForJdbcParam(sql+"order by id" , dataGrid.getPage(), dataGrid.getRows());
        int rows = dataGrid.getRows();
        totalPage = total % rows == 0 ? (total / rows) : (total / rows + 1);
        resultMapList.get(0).put("totalPage",totalPage);
    }
}else{
    resultMapList=this.findForJdbc(sql+"order by id");
}
return resultMapList;

